import React, { Component } from "react";
import styled from "styled-components";


const Container = styled.div`
max-width: 1360px;
padding-right: 15px;
padding-left: 15px;
margin-right: auto;
margin-left: auto;
box-sizing: border-box;
background-color: red;
&:before,
&:after {
    content: "";
    display:table;
    }
    &:after {
        clear: both;
    }
`
export const Row = styled.div`
width: 100%;
height: auto;
float: left;
box-sizing: border-box;
&:after {
    clear: both;
}
`;

const Column = styled.div`
float: left;
padding: .25rem;
min-height: 1px;
box-sizing: border-box;
width: 100%;

@media only screen and (min-widht: 768px) {
    width: ${props => (props.grid ? props.grid /12 * 100 : '8:33')}%;
}
`;


class App extends Component {
    render() {
        return(
            <Container>
                <Row>
                    <Column grid='4'>grid 5</Column>
                    <Column grid='5'>grid 5</Column>
                    <Column grid='2'>grid 2</Column>
                </Row>
            </Container>
        );
    }
}
export default App;
