const { useState, useEffect } = require("react")
import styled from "styled-components";

const TileWrapper = styled.div`
    background-color: white;
    padding: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    border: 2px solid gray;
    width: 200px;
    height: 200px;
    padding: 10px;
    img{
        width:150px;
        height:150px;
    }
    `;

    const PokemonName = styled.div`
        background: gray;
        width: 100%;
        position: absolute;
        left: 0;
        bottom: 0;
        color: #fff;
        text-align: center;
        `;
        
        export default function PokemonTile({ pokemon }) {

            useEffect(() => {
              console.log("opa")  
            }, [])

            return (
                <TileWrapper>
                    <img src={"https://pngimg.com/uploads/pokeball/pokeball_PNG21.png"} />
                    <PokemonName>{pokemon.name ? pokemon.name : "unknown"}</PokemonName>
                </TileWrapper>
            );
        }
