import React from "react";
import styled from "styled-components";
import GetPokemonData from "../Utils/GetPokemonData";
const { useState, useEffect } = require("react")

const Qualquer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  `;

const Image = styled.div`
    width: 600px;
    height: 600px;
    margin: 15px;
     `;

    const TileTypeMiss = styled.div`
    display: flex;
    align-items: center;
    height: 30px;
    width: 80px;
    border-radius: 15px;
    border: 1px solid black;
    color: black;
    
    `;


export default function PokemonData({ pokemon }) {
    const [PokemonInfo, setPokemonInfo] = useState({})
useEffect(() => {
    GetPokemonData(pokemon.url)
    .then(( success ) => {
        setPokemonInfo(success)
    })
    .catch((error) => {
        console.log(error)
    });
}, []);
    return (
        <Qualquer>
            <Image>
            <img src={PokemonInfo.sprites ? PokemonInfo.sprites.front_default : "https://sites.google.com/site/pokemondpcombr/_/rsrc/1472844548918/home/equipe-rocket/Equipe%20Rocket.png"} />
            </Image>
        </Qualquer>
    );
    }
