import React, { Component } from "react";
import styled from "styled-components";

const GridWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  `;

const TileWrapper = styled.div`
background-color: white;
padding: 15px;
display: inline-flex;
align-items: center;
justify-content: center;
position: relative;
border: 1px solid #C1BFC2;
width: 200px;
height: 200px;
margin: 15px;
`
