import axios from "axios";

export function GetPokemon(){
    return new Promise((resolve, reject) =>{
        axios
         .get("https://pokeapi.co/api/v2/pokemon?limit=151")
         .then((success) => resolve(success))
         .catch((error) => reject(error));
    });
}
