import React, { useState, useEffect, Component } from "react";
import styled from "styled-components";
import PokemonTile from "../Components/PokemonCard";
import ListPokemon from "../Utils/ApiPokemons";
import {hashRouter as Router, Route, Switch} from 'react-dom';

const GridWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  `;

  export default function index() {
    const [ListaPokemon, setListaPokemon] = useState([])

    
    function PokemonSelected(pokemonNameUrl){
      setPokemonSelected(pokemonNameUrl)
      router.push('./PokemonSolo')}
    
    
    useEffect(() => {
      ListPokemon().then( (success) => {
        setListaPokemon(success.results);
      })
    }, [])

    return (
      <GridWrapper>
        {ListaPokemon.map((pokemon) => {
          return <div onClick={()=> PokemonSelected(pokemonNameUrl)}>
            <PokemonTile pokemon={pokemon} />
          </div>
        })}
        
      </GridWrapper>
    );
  }
