import { createGlobalStyle, ThemeProvider } from 'styled-components'
import "../Styles/index.css"

const GlobalStyle = createGlobalStyle`
body {
    margin: 15px;
    padding: 0px;
    box-sizing: border-box;
    background-image: "../Public/Background.png";
    font-family: 'skolaSans';
    
}
`
