import React, { useState, useEffect, Component } from "react";
import styled from "styled-components";
import PokemonTile from "../Components/PokemonCard";
import ListPokemon from "../Utils/ApiPokemons";
import PokemonSolo from "../Components/PokemonSolo"

const GridWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  `;

  export default function index() {
    const [ListaPokemon, setListaPokemon] = useState([])
    
    
    useEffect(() => {
      ListPokemon().then( (success) => {
        setListaPokemon(success.results);
        console.log("Deu Bom", success);
      })
    }, [])

    function PokemonInfo(PokemonNameUrl){
      setPokemoninfo(PokemonNameUrl)
      router.push('./PokemonInfo')
    }

    return (
      <GridWrapper>
       <PokemonSolo />
               
      </GridWrapper>
    );
  }
