const { useState, useEffect } = require("react")
import styled from "styled-components";

const TileWrapper = styled.div`
    padding: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    border: 2px solid gray;
    width: 200px;
    height: 200px;
    img{
        width:50px;
        height:50px;
    }
    `;

    const PokemonName = styled.div`
        background: gray;
        width: 100%auto;
        position: absolute;
        left: 0;
        bottom: 0;
        color: #fff;
        text-align: center;
        `;
        
        export default function PokemonTile({ pokemon }) {
            return (
                <TileWrapper>
                    <img src={"https://www.clipartmax.com/png/middle/129-1298476_free-icons-png-pokeball-transparent-png.png"} />
                    <PokemonName>{pokemon.name ? pokemon.name : "???????"}</PokemonName>
                </TileWrapper>
            );
        }
