import axios from "axios";

export default async function PokemonTypes(){
    return new Promise((resolve, reject) =>{
        axios
         .get("https://pokeapi.co/api/v2/type/{id or name}/")
         .then((res) => resolve(res.data))
         .catch((error) => reject(error));
    });
}
